    font-size: 1.6em; 
    margin: 10px 0;
    font-weight: 400;
    justify-content: center;
    text-align: center;
}

md4 {
    font-size: 1.1em; 
}

md5 {
    font-size: 1em; 
    color: #222222;
    justify-content: center;
    text-align: center;
}

textarea {
    width:98%;
    height: auto;
    min-height: 54px;
    max-height: 500px;
    overflow-y: auto;
    border-radius:5px;
    border: 1px solid #BDBDBD;
}

.button {
    display: block; 
    margin-left: auto;
    margin-right: auto;
    padding: 6px 10px;
    background-color: #FF5722;
    border: none;
    color: white;
    cursor: pointer;
    border-radius: 4px;
    text-decoration: none;
    margin-top: 1px;
    font-size: 18px;
    box-shadow: 0 2px 5px rgba(0, 0, 0, 0.2);
    transition: background-color 0.5s,
    transform 0.4s;
}

.button:hover {
    background-color: #FF5722;
    transform: translateY(-2px);
}

.button:active {
    box-shadow: 0 3px 5px rgba(0, 0, 0, 0.4);
    transform: translateY(0);
}

