<js>
li='\n'
list=result.split('$')
for(i in list){
		li += '<li><img src="https://gd-hbimg.huaban.com/'+list[i]+'"></li>\n'
	}

</js>

<meta name="viewport" content="width=device-width, initial-scale=1.0, maximum-scale=1.0, user-scalable=0">
<title>{{$.board.title}}</title>
<ul id="box">
{{result}}
</ul>
<link rel="stylesheet" href="https://cdn.bootcdn.net/ajax/libs/viewerjs/1.10.1/viewer.min.css">
<script src="https://cdn.bootcdn.net/ajax/libs/viewerjs/1.10.1/viewer.min.js"></script>
<script src="http://lib.sinaapp.com/js/jquery/2.0.2/jquery-2.0.2.min.js"></script>
<script>
var i = 1 ;
$(document).ready(function () {
root = document.getElementById('box');
//æŸ¥çœ‹å¤§å›¾æ’ä»¶
new Viewer(root,{title:false,interval:3000});
var url = '{{baseUrl}}/relatedboards';
fetchHtml(3, url)
 });
function fetchHtml(i, url) {
            nurl = url + "?limit=" + i;
            $.ajax({
                url: nurl,
                dataType: 'json',
                success: function (json) {
                    for (var k = 0; k < json.boards.length; k++) {
                        for (var q = 0; q < json.boards[k].pins.length; q++) {
                            $('#box').append('<li><img src=\"https://gd-hbimg.huaban.com/' + json.boards[k].pins[q].file.key + '\"></li>\n');
                        }
                    }
                    box.viewer.update();
                },
                error: function (xhr) { }
            });
        }
</script>

