$..vodPlayUrl
<js>
// 更新状态
a = "{{$..vodRemarks}}";
b = "{{$..vodTotal}}";
if(!a.match(/完结/)&&!b.match(/^0*$/))a+="(共"+b+"集)";
gx = a+"{{$..vodPubdate##(.+)## ∙ $1}}";


// 格式化解析
jiexi = eval(String(source.sourceComment)).map($=>'<button onclick="xuan(this,\'.jiexi\')" value="'+$[1]+'"><b>'+$[0]+'</b></button>');


// 格式化集数
jishu = result.split('#').map($=>{
		$ = $.split('$');
		return '<button onclick="xuan(this,\'.jishu\')" value="'+$[1]+'"><b>'+$[0]+'</b></button>'
	});



// 返回json
JSON.stringify({
	gx: gx,
	jiexi: '<div>\n\t\t'+jiexi.join('\n\t\t')+'\n</div>',
	jishu: '<div>\n\t\t'+jishu.join('\n\t\t')+'\n</div>'
});
</js>
<title>{{$..vodName}}</title>
<meta name="viewport" content="width=device-width,initial-scale=1.0,minimum-scale=1.0,maximum-scale=1.0,user-scalable=no">
<iframe frameborder="no" allowfullscreen></iframe>
<p></p>
<hr>


<details>
<summary><h3>{{$..vodName}}</h3></summary>
<br>

<!--
<img src="{{$..vodPic}}" onerror="this.src='{{source.sourceIcon}}'">
<p>片名：{{$..vodName}}</p>
<p>导演：{{$..vodDirector##,##, }}</p>
<p>编剧：{{$..vodWriter##,##, }}</p>
<p>主演：{{$..vodActor##,##, }}</p>
-->


<p>📜 又名：{{$..vodSub}}</p>
<p>🔮 类型：{{$..vodArea&&$..vodLang&&$..vodYear##\n## ∙ }}</p>
<p>🔥 热度：{{$..vodHits}}</p>
<p>📝 评分：网站{{$..vodScore}} | 豆瓣{{$..vodDoubanScore}}</p>
<p>🔖 更新：{{JSON.parse(result).gx}}</p>
<p>🏷️ 标签：{{$..vodClass&&$..vodTag##\n|,##, }}</p>
<br>
<p>📑 简介：{{$..vodContent##\s*(^|[。！？]+[”」）】]?)\s*##$1<br>}}</p>
<br>
</details>
<hr>


<div class="jiexi">
<p>解析</p>
{{JSON.parse(result).jiexi}}
</div>
<hr>


<div class="jishu">
<p>集数</p>
{{JSON.parse(result).jishu}}
</div>
<hr>





<script>
//选中标签
$=(rule)=>document.querySelectorAll(rule);

//删除active
omit=(A)=>Array.from(A,a=>a.className='');

//增加active
active=($,i)=>$[i].className='active';

//选择集数
xuan = (item,type)=>{
		btn = $(type+' button.active');

		if(item.value==btn[0].value)return;
		omit(btn); item.className='active'
		return geturl()
	}


//拼接链接
val=(type)=>$(type+' button.active')[0].value.trim();
geturl=()=>
	$('iframe')[0].src=$('p')[0].innerText=
		val('.jiexi') + val('.jishu');


//初始化
(()=>{
		active($('.jiexi button'),0)
		active($('.jishu button'),0)
		return geturl()
	})()
</script>## ∙ 0+(\s*)##$1

