var exploreUrl = [];
var Sort = `{{get('sort',Get('q'))}}`;
var Sort1 = `{{get('sort1',Get('r'))}}`;
function explore(e, f, g) {
    if (g=='收藏') {
        style = `${e}::@js:\`${f}\`\n`;
    } else if (g=='主题') {
        style = `${e}::@js:\`${f}?mode=async&function=get_block&block_id=list_videos_common_videos_list&sort_by=${Sort}&from={{page}}&_={{new Date().getTime()}}\`\n`;
    } else if (g=='标签') {
        style = `${e}::@js:\`${f}?mode=async&function=get_block&block_id=list_videos_common_videos_list&sort_by=${Sort}&from={{page}}&_={{new Date().getTime()}}\`\n`;
    } else {//女优
        style = `${e}::@js:\`${f}?mode=async&function=get_block&block_id=list_videos_common_videos_list&sort_by=${Sort}&from={{page}}&_={{new Date().getTime()}}\`\n`;
    }
    return style;
}

sc = Map('收藏：');
scs = sc !== '' ? sc.split(",") : [];
if (scs.length > 0) {
    scs.forEach((sc) => {
        let scHref = `{{Get('url')}}/search/${sc}/?mode=async&function=get_block&block_id=list_videos_videos_list_search_result&q=${sc}&sort_by=${Sort1}&from={{page}}&_={{new Date().getTime()}}`;
        exploreUrl.push(explore(sc, scHref, '收藏'));
    });
}

html = java.ajax(Get('url')+'/categories/');
if(String(html).includes('no-js')){
    cookie.removeCookie(Get('url'));
    java.startBrowserAwait(Get('url'),'验证');
}

J = org.jsoup.Jsoup.parse(html);

if (Get('o') == 0) {
    java.toast("正在获取主题标签数据，请稍后！")
    categories = J.select('.pb-e-lg-40 a');
    for (i in categories) {
        exploreUrl.push(explore(categories[i].select('h4').text()+'('+String(categories[i].select('span').text()).replace(/(\d+).*$/, '$1')+')', `{{Get('url')}}${String(categories[i].attr('href')).replace(/.*\/\/[a-z0-9.-]+/, '')}`, '主题'));
    }
    tags = J.select('.gutter-20.pb-3 a');
    for (i in tags) {
        exploreUrl.push(explore(tags[i].text(), `{{Get('url')}}${String(tags[i].attr('href')).replace(/.*\/\/[a-z0-9.-]+/, '')}`, '标签'));
    }
}

nsort = ['avg_videos_popularity','title','last_content_date','total_videos'];
