    Fpage = /-/.test(Map('页数：')) ? Map('页数：').match(/(\d+)/g)[0] : '1';
    Lpage = /-/.test(Map('页数：')) ? Map('页数：').match(/(\d+)/g)[1] : (/\d+/.test(Map('页数：')) ? Map('页数：') : '5');
    var murl = [];
    for (let i = Fpage; i <= Lpage; i++) {
        murl.push(Get('url')+'/models/?mode=async&function=get_block&block_id=list_models_models_list&sort_by='+nsort[Get('p')]+'&from='+i+'&_='+(new Date().getTime())); 
    }
    htmls = java.ajaxAll(murl);
    for (let i = 0; i < htmls.length; i++) {
        let models = org.jsoup.Jsoup.parse(htmls[i].body()).select('.horizontal-img-box');
        models.forEach((model) => {
            let modelName = model.select('.title').text();
            let modelSpanText = model.select('span').text();
            let modelVideosCount = String(modelSpanText).replace(/(\d+).*$/, '$1');
            let modelHref = `{{Get('url')}}${String(model.select('a').attr('href')).replace(/.*\/\/[a-z0-9.-]+/, '')}`;
