       img.setAttribute('referrerpolicy', 'no-referrer');
        picDiv.appendChild(img);

        var detailsDiv = document.createElement('div');
        detailsDiv.classList.add('details');

        var bookNameP = document.createElement('p');
        bookNameP.classList.add('bookname');

        var nameSpan = document.createElement('span');
        nameSpan.classList.add('name');
        let novelid = "https://m.jjwxc.net/book2/"+item.novelid;
        var link = document.createElement('a');
        link.href = novelid;
        link.textContent = item.novelname;
        nameSpan.appendChild(link);

        bookNameP.appendChild(nameSpan);
        bookNameP.innerHTML += '<br>';

        var authorSpan = document.createElement('span');
        authorSpan.classList.add('author');
        authorSpan.textContent = '——' + item.authorname;
        bookNameP.appendChild(authorSpan);

        var tagP = document.createElement('p');
