    // 定义跳转函数
    function jumpToPage() {
      // 获取当前 URL
      let currentUrl = new URL(window.location.href);

      // 获取输入的页码
      let page = parseInt(pageInput.value);

      // 计算 offset 值
      let offset = (page - 1) * 20;

      // 更新 URL 中的 offset 参数
      currentUrl.searchParams.set('offset', offset);

      // 跳转至新的 URL
      window.location.href = currentUrl.toString();
      updateCurrentPage(page);
    }

    // 上一页按钮点击事件
    prevButton.addEventListener('click', function () {
      // 获取当前 URL
      let currentUrl = new URL(window.location.href);

      // 获取当前的 offset 值
      let currentOffset = parseInt(currentUrl.searchParams.get('offset'));

      // 计算新的 offset 值（减少 20）
      let newOffset = Math.max(0, currentOffset - 20);

      // 更新 URL 中的 offset 参数
      currentUrl.searchParams.set('offset', newOffset);

      // 跳转至新的 URL
      window.location.href = currentUrl.toString();
      updateCurrentPage(getCurrentPage(newOffset));
    });

    // 下一页按钮点击事件
    nextButton.addEventListener('click', function () {
      // 获取当前 URL
      let currentUrl = new URL(window.location.href);

      // 获取当前的 offset 值
      let currentOffset = parseInt(currentUrl.searchParams.get('offset'));

      // 计算新的 offset 值（增加 20）
      let newOffset = currentOffset + 20;

      // 更新 URL 中的 offset 参数
      currentUrl.searchParams.set('offset', newOffset);

      // 跳转至新的 URL
      window.location.href = currentUrl.toString();
      updateCurrentPage(getCurrentPage(newOffset));
    });

    // go 按钮点击事件
    goButton.addEventListener('click', jumpToPage);
    // 根据 offset 值计算当前页码
    function getCurrentPage(offset) {
      return Math.floor(offset / 20) + 1;
    }

    // 更新当前页码的显示
    function updateCurrentPage(page) {
      currentPageSpan.textContent = page;
    }

    // 初始时获取当前 offset 值并计算并显示当前页码
    let currentUrl = new URL(window.location.href);
    let currentOffset = parseInt(currentUrl.searchParams.get('offset'));
    updateCurrentPage(getCurrentPage(currentOffset));
