if(/[\?#&]book[AN]/.test(url)){
	title = url.match(/[\?#&]book[AN].+?=(.*)/)[1];
	URLDecoder = Packages.java.net.URLDecoder;
	gb2312Str = URLDecoder.decode(title,"gbk");
 utf8Str = URLDecoder.decode(title,"utf-8");
	 result = gb2312Str.length() >= utf8Str.length();
	 title = result?decodeURI(title):gb2312Str;
	 java.searchBook(title);
	 result = true
	}

