const fixBookClick = setInterval(() => {
  const shouldSkip = (el) => {
    return el.closest('.horizontal-slip-modal-card') || 
           el.closest('.topic-comment-item.new-card-style');
  };

  document.querySelectorAll('.horizontal-slip-books-item').forEach(item => {
    if (shouldSkip(item)) return;

    item.style.pointerEvents = 'auto';
    item.style.cursor = 'pointer';

    if (item.id && !item.hasAttribute('data-click-fixed')) {
      item.setAttribute('data-click-fixed', 'true');
      item.addEventListener('click', (e) => {
        e.preventDefault();
        window.location.href = `https://fanqienovel.com/page/${item.id}`;
      });
    }
  });

  document.querySelectorAll('div.books-fold-card-book-item.marrow-book-item').forEach(item => {
    if (shouldSkip(item)) return;

    item.style.pointerEvents = 'auto';
    item.style.cursor = 'pointer';

    const bookId = item.getAttribute('data-book-id') || item.id;
    if (bookId && !item.hasAttribute('data-click-fixed')) {
      item.setAttribute('data-click-fixed', 'true');
      item.addEventListener('click', (e) => {
        e.preventDefault();
        window.location.href = `https://fanqienovel.com/page/${bookId}`;
      });
    }
  });
}, 1000);

const paginationSystem = setInterval(() => {
  const shareBottom = document.querySelector(".share-bottom-button");
  const shouldSkip = (el) => {
    return el && (el.closest('.horizontal-slip-modal-card') || 
                 el.closest('.topic-comment-item.new-card-style'));
  };

  if (shareBottom && !shouldSkip(shareBottom)) {
    shareBottom.remove();
    const shareApp = document.querySelector(".share-end-href-app");
