    function onTouchStart(evt) {
      for (const el of evt.path) {
        if (el.tagName === "A") {
          if (el.href !== 'javascript:void(0)') {
            const inner = el.href;
            herfs.set(el, inner);
            el.setAttribute('href', 'javascript:void(0)');
