        item.addEventListener('touchend', onTouchEnd);
        item.addEventListener('touchcancel', onTouchEnd);
        item.addEventListener('mousedown', onTouchStart);
        item.addEventListener('mouseup', onTouchEnd);
      }
    });
num= els.length;
}

function reAdd(){
