ruleContent:
class.ss-image@href
<js>
li='\n'
list=result.split('\n')
for(i in list){
		li += '<li><img src="http://bpic.wotucdn.com/20/32/52/20325281-1190f9105412b42f759882ce262c2dcb-3.jpg" data-src="'+list[i]+'"></li>\n'
	}
li
</js>
<meta name="viewport" content="width=device-width, initial-scale=1.0, maximum-scale=1.0, user-scalable=0">


<ul id="box">
{{result}}
</ul>


<link rel="stylesheet" href="https://cdn.bootcdn.net/ajax/libs/viewerjs/1.10.1/viewer.min.css">
<script src="https://cdn.bootcdn.net/ajax/libs/viewerjs/1.10.1/viewer.min.js"></script>
<script>
	root = document.getElementById('box')
	lazy = new IntersectionObserver(entires => {
		entires.forEach(item => {
			if(item.isIntersecting){
				item=item.target
				item.src=item.getAttribute('data-src')
				lazy.unobserve(item)
			}
		})
	}, {
		root: root,
		threshold: [0],
		rootMargin:"0px 0px 500px 0px"
	})

imgs = document.getElementsByTagName('img')
Array.from(imgs).forEach(item => {
		lazy.observe(item)
	})

//æŸ¥çœ‹å¤§å›¾æ’ä»¶
new Viewer(root,{title:false,interval:3000})
</script>

